namespace AoC2022;

public class Day6 : Day
{
    public override string Title => "--- Day 6: Tuning Trouble ---";

    public override void PartOne()
    {
        base.PartOne();
        var input = Input;
        //input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"; // 7
        //input = "bvwbjplbgvbhsrlpgdmjqwftvncz"; // 4
        //input = "nppdvjthqldpwncqszvftbrmjlhg"; // 6
        //input = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"; // 10
        //input = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"; // 11

        var s = input.AsSpan();

        var result = 0;
        for (var i = 4; i < s.Length; i++)
        {
            var lower = i - 4;
            var lastFour = s[lower..i];

            if (!lastFour[1..].Contains(lastFour[0]) &&
                !lastFour[2..].Contains(lastFour[1]) &&
                !lastFour[3..].Contains(lastFour[2]))
            {
                result = i; 
                break;
            }
        }

        Console.WriteLine(result); // 1566
    }

    public override void PartTwo()
    {
        base.PartTwo();
        var input = Input;
        //input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"; // 19

        var s = input.AsSpan();

        var result = 0;
        for (var i = 14; i < s.Length; i++)
        {
            var lower = i - 14;
            var lastFour = s[lower..i];

            var gotMatch = false;
            for (var j = 1; j < 13; j++)
            {
                if (lastFour[j..].Contains(lastFour[j - 1]))
                {
                    gotMatch = true;
                    break;
                }
            }

            if (gotMatch)
            {
                continue;
            }
            
            result = i;
            break;
        }

        Console.WriteLine(result); // 2265
    }

    private static string Input = @"";
}